<script>
var hour = moment().hour();
var minute = moment().minute();
var meridien = moment().format("A");
var h = [12,13,14,15,16];

$(".fullname").html($(".fullname").html());
if(meridien == "AM"){ $(".meridien").html("morning") }
else{
    if(h.includes(hour)){ $(".meridien").html("afternoon") }
    else{ $(".meridien").html("evening") }
}
</script>

